import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const ProductImages = ({ product }) => {
  const settings = {
    arrows: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <Slider {...settings}>
      <div>
        <img src={product.img} alt={product.name} className="w-full" />
      </div>
      <div>
        <video controls src={product.video} className="w-full" />
      </div>
      {product.colorsImg.map((colorImg, index) => (
        <div key={index}>
          <img src={`https://ae01.alicdn.com/kf/${colorImg}`} alt={`Color ${index + 1}`} className="w-full" />
        </div>
      ))}
    </Slider>
  );
};

export default ProductImages;
