import React, { useEffect, useState } from "react";
import OrderServiceInstance from "../../Services/OrderService";


const OrderCheckout = () => {

    const [order, setOrder] = useState();

    useEffect(() =>{
        handleFetchOrder();
    })
    const handleFetchOrder = () =>{
        if(localStorage.getItem('orderId')){
           OrderServiceInstance.getOrderById(localStorage.getItem('orderId'))
            .then((response) => {
                console.log(response.data)
                setOrder(response.data);    
            }).catch((error) => {
                console.log('Error fetching order with ID: ', localStorage.getItem('order'));
            })

        }else{
            console.log("dont have any order yet");
        }
    }



    return (
        <div className="min-w-screen min-h-screen bg-gray-50 py-5">
          <style>
            @import url('https://cdnjs.cloudflare.com/ajax/libs/MaterialDesign-Webfont/5.3.45/css/materialdesignicons.min.css');
          </style>
          <div className="px-5">
            <div className="mb-2">
              <a href="#" className="focus:outline-none hover:underline text-gray-500 text-sm">
                <i className="mdi mdi-arrow-left text-gray-400"></i> Wróć
              </a>
            </div>
            <div className="mb-2">
              <h1 className="text-3xl md:text-5xl font-bold text-gray-600">Zamówienie {order.id}</h1>
            </div>
          </div>
          <div className="w-full bg-white border-t border-b border-gray-200 px-5 py-10 text-gray-800">
            <div className="w-full">
              <div className="-mx-3 md:flex items-start">
                <div className="px-3 md:w-7/12 lg:pr-10">
                  <div className="w-full mx-auto text-gray-800 font-light mb-6 border-b border-gray-200 pb-6">
                    {order.lines.map((product) => (
                      <div key={product.id} className="w-full flex items-center">
                        <div className="overflow-hidden rounded-lg w-16 h-16 bg-gray-50 border border-gray-200">
                          <img src={product.image} alt={product.productName} />
                        </div>
                        <div className="flex-grow pl-3">
                          <h6 className="font-semibold uppercase text-gray-600">
                            {product.productName}
                          </h6>
                          <p className="text-gray-400">x {product.quantity}</p>
                        </div>
                        <div>
                          <span className="font-semibold text-gray-600 text-xl">
                            ${product.price}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                  <div className="mb-6 pb-6 border-b border-gray-200 text-gray-800 text-xl">
                    <div className="w-full flex items-center">
                      <div className="flex-grow">
                        <span className="text-gray-600">Razem</span>
                      </div>
                      <div className="pl-3">
                        <span className="font-semibold">${order.lines.reduce((acc, product) => acc + product.price * product.quantity, 0)}</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    };
export default OrderCheckout;
