import React from 'react';
import Slider from 'react-slick';

const ProductImages = ({ product }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
    arrows: true,
    adaptiveHeight: true,
  };

  return (
    <div>
      <Slider {...settings}>
        <div>
          <iframe
            title="product-video"
            src={product.video}
            width="100%"
            height="400"
            frameBorder="0"
            allowFullScreen
          ></iframe>
        </div>
        {product.colorsImg.map((colorImg, index) => (
          <div key={index}>
            <img src={colorImg} alt={`Color ${index + 1}`} style={{ width: '100%' }} />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default ProductImages;
