import React from 'react';
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';
import pt from './pt.png'
import pg from './pg.png'
import a from './a.png'
import z from './z.png'
import baner from "./slBANER.png"
// const items = [
//     <img src={pt} data-value="1"></img>,
//     <img src={pg} data-value="2"></img>,
//     <img src={z} data-value="3"></img>,
//     <img src={a} data-value="4"></img>,
// ];

// const Carousel = () => (
//     <AliceCarousel
//         autoPlay
//         autoPlayControls
//         autoPlayStrategy="none"
//         autoPlayInterval={3000}
//         animationDuration={1000}
//         animationType="fadeout"
//         infinite
//         touchTracking={false}
//         disableDotsControls
//         disableButtonsControls
//         items={items}
//     />
// );
const items = [
    { image: pt, link: 'https://example.com/page1' },
    { image: pg, link: 'https://example.com/page2' },
    { image: z, link: 'https://example.com/page3' },
    { image: a, link: 'https://example.com/page4' },
    { image: baner, link: '#' },
];

const Carousel = () => {
    const handleImageClick = (link) => {
        window.location.href = link;
    };

    const renderItems = () => {
        return items.map((item, index) => (
            <img
                key={index}
                src={item.image}
                onClick={() => handleImageClick(item.link)}
                data-value={index + 1}
                style={{ width: '300%', height: 'auto', objectFit: 'cover' }}
            />
        ));
    };
    
    return (
        <div className="mx-auto max-w-7xl px-6 pt-8 lg:px-8 lg:pt-12" style={{ marginBottom: '-100', padding: '0' }}>
        <AliceCarousel
            autoPlay
            autoPlayControls
            autoPlayStrategy="none"
            autoPlayInterval={5000}
            animationDuration={2000}
            animationType="fadeout"
            infinite
            touchTracking={false}
            disableDotsControls
            disableButtonsControls
            items={renderItems()}
            responsive={{ 0: { items: 1 }, 768: { items: 2 }, 1024: { items: 3 } }}
        />
    </div>
);
};

export default Carousel;
