import React, { useState } from 'react';
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';

const ProductImages = ({ product }) => {
    const [mainIndex, setMainIndex] = useState(0);
    const [thumbIndex, setThumbIndex] = useState(0);
    const [thumbs] = useState(thumbItems(product));

    const thumbItems = (product) => {
        const items = product.colorsImg.map((img, index) => (
            <img key={index} src='https://ae01.alicdn.com/kf/${img}' style={{ maxWidth: '100%', height: 'auto' }} />
        ));
        return items;
    };

    const slideNext = () => {
        if (thumbIndex < thumbs.length - 1) {
            setThumbIndex(thumbIndex + 1);
        }
    };

    const slidePrev = () => {
        if (thumbIndex > 0) {
            setThumbIndex(thumbIndex - 1);
        }
    };

    const syncThumbs = (e) => {
        setThumbIndex(e.item);
        setMainIndex(e.item);
    };

    return (
        <div className="relative">
            <AliceCarousel
                activeIndex={mainIndex}
                animationDuration={800}
                disableDotsControls
                disableButtonsControls
                items={[
                    <iframe
                        title="product-video"
                        src={product.video}
                        width="100%"
                        height="400"
                        frameBorder="0"
                        allowFullScreen
                    ></iframe>,
                    ...product.colorsImg.map((img, index) => (
                        <img key={index} src={img} style={{ maxWidth: '100%', height: 'auto' }} />
                    ))
                ]}
                onSlideChanged={syncThumbs}
            />
            <div className="thumbs">
                <AliceCarousel
                    activeIndex={thumbIndex}
                    autoWidth
                    disableDotsControls
                    disableButtonsControls
                    items={thumbs}
                    mouseTracking={false}
                    onSlideChanged={syncThumbs}
                />
                <div className="btn-prev" onClick={slidePrev}>&lang;</div>
                <div className="btn-next" onClick={slideNext}>&rang;</div>
            </div>
        </div>
    );
};

export default ProductImages;
